INSERT INTO `bm_article_body` VALUES ('1468231964527546370', '~~~ java /**\n     * user转换为loginVo\n     * @param sysUser\n     * @return\n     */\n    private LoginVo userToLoginVo(SysUser sysUser) {\n        LoginVo loginVo =new LoginVo();\n        BeanUtils.copyProperties(sysUser, loginVo);\n        return loginVo; ', '<pre><div class=\"hljs\"><code class=\"lang-java\">     * user转换为loginVo\n     * <span class=\"hljs-meta\">@param</span> sysUser\n     * <span class=\"hljs-meta\">@return</span>\n     */\n    <span class=\"hljs-function\"><span class=\"hljs-keyword\">private</span> LoginVo <span class=\"hljs-title\">userToLoginVo</span><span class=\"hljs-params\">(SysUser sysUser)</span> </span>{\n        LoginVo loginVo =<span class=\"hljs-keyword\">new</span> LoginVo();\n        BeanUtils.copyProperties(sysUser, loginVo);\n        <span class=\"hljs-keyword\">return</span> loginVo; </code></div></pre>\n', 0, 0, '1468231964523352066', 0, '2022-01-27 21:31:42', '2022-01-27 21:31:42');
