-- Table structure for bm_comment
-- ----------------------------
DROP TABLE IF EXISTS `bm_comment`;
CREATE TABLE `bm_comment`  (
  `id` char(29) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT 'id',
  `content` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '评论内容',
  `article_id` char(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '文章ID',
  `author_id` char(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '作者ID',
  `parent_id` char(20) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '父评论ID',
